import React from "react";

const Login = ()=>{

    const navigate = useNavigate();

    const [form, setformdata] = React.useState({
        name: '',
        password: '',
    });
    const handleForm = e => {
        let name = e.target.name;
        let val = e.target.value;
        setformdata({ ...form, [name]: val })
        console.log(form);
    }
    const login = () => {
        // let params = new URLSearchParams()
        axios.post(baseurl, {
            name: form.name,
            password: form.password
        }).then(res => {
            console.log(res)
                // alert(res.data.message);
                navigate('/dashboard')
        }).then(result => console.log(result))
            .catch(err => {
                console.log(err);
                if (err.response.status === 401) {
                    alert("Invalid Password");
                }
                else if (err.response.status === 400) {
                    alert("User not found");
                }
            })
    }
    return (
        <>
            <h1>LOGIN</h1>
            <hr />
            <form>
                <div><input type="text" name="name" id="name" value={form.name} placeholder="name" onChange={handleForm} required /></div>
                <div><input type="password" name="password" id="password" value={form.password} placeholder="password" onChange={handleForm} required /></div>
                <div><button type="button" className="btn btn-primary" onClick={login}>Login</button> </div>
            </form>
            <Link to="/">Register</Link>
        </>
    );
};

export default Login
